// PT chart data (simplified)
const ptData = {
  r22: [
    {pressure: 0, temperature: -41},
    {pressure: 10, temperature: -26},
    {pressure: 30, temperature: -3},
    {pressure: 50, temperature: 12},
    {pressure: 70, temperature: 24},
    {pressure: 90, temperature: 34},
    {pressure: 110, temperature: 43},
    {pressure: 130, temperature: 50},
    {pressure: 150, temperature: 57},
    {pressure: 170, temperature: 64},
    {pressure: 200, temperature: 73},
    {pressure: 225, temperature: 80},
    {pressure: 250, temperature: 87},
    {pressure: 275, temperature: 93},
    {pressure: 300, temperature: 99}
  ],
  r410a: [
    {pressure: 0, temperature: -70},
    {pressure: 25, temperature: -40},
    {pressure: 50, temperature: -17},
    {pressure: 75, temperature: 0},
    {pressure: 100, temperature: 13},
    {pressure: 125, temperature: 24},
    {pressure: 150, temperature: 34},
    {pressure: 175, temperature: 43},
    {pressure: 200, temperature: 51},
    {pressure: 225, temperature: 58},
    {pressure: 250, temperature: 65},
    {pressure: 275, temperature: 71},
    {pressure: 300, temperature: 76}
  ],
  r134a: [
    {pressure: 0, temperature: -15},
    {pressure: 10, temperature: 4},
    {pressure: 20, temperature: 19},
    {pressure: 30, temperature: 30},
    {pressure: 40, temperature: 39},
    {pressure: 50, temperature: 48},
    {pressure: 60, temperature: 55},
    {pressure: 70, temperature: 62},
    {pressure: 80, temperature: 68},
    {pressure: 90, temperature: 73},
    {pressure: 100, temperature: 78},
    {pressure: 125, temperature: 89},
    {pressure: 150, temperature: 99},
    {pressure: 175, temperature: 108},
    {pressure: 200, temperature: 116}
  ]
};

// Initialize the demo
function initPTDemo() {
  const refrigerantSelect = document.getElementById('refrigerant-select');
  const pressureSlider = document.getElementById('pressure-slider');
  const pressureValue = document.getElementById('pressure-value');
  const temperatureResult = document.getElementById('temperature-result');
  const stateResult = document.getElementById('state-result');

  // Update results based on inputs
  function updateResults() {
    const refrigerant = refrigerantSelect.value;
    const pressure = parseInt(pressureSlider.value);

    // Update pressure display
    pressureValue.textContent = pressure;

    // Find temperature based on pressure
    const data = ptData[refrigerant];
    let temperature;

    // Find the closest pressure points and interpolate
    for (let i = 0; i < data.length - 1; i++) {
      if (pressure >= data[i].pressure && pressure <= data[i+1].pressure) {
        const p1 = data[i].pressure;
        const p2 = data[i+1].pressure;
        const t1 = data[i].temperature;
        const t2 = data[i+1].temperature;

        // Linear interpolation
        temperature = t1 + (pressure - p1) * (t2 - t1) / (p2 - p1);
        break;
      }
    }

    // Handle edge cases
    if (temperature === undefined) {
      if (pressure < data[0].pressure) {
        temperature = data[0].temperature;
      } else {
        temperature = data[data.length - 1].temperature;
      }
    }

    // Round to one decimal place
    temperature = Math.round(temperature * 10) / 10;

    // Update display
    temperatureResult.textContent = `${temperature}Â°F`;
    stateResult.textContent = 'Saturated';
  }

  // Set up event listeners
  refrigerantSelect.addEventListener('change', updateResults);
  pressureSlider.addEventListener('input', updateResults);

  // Initialize with default values
  updateResults();
}

// Run the demo
initPTDemo();
